INSERT INTO public.email_templates (template_id, name, subject, html_content, text_content) VALUES
('GROUPBUY_SUCCESS_PAYMENT_REQUIRED', 'Group Buy Success - Payment Required', 'Success! Pay Now to Claim Your Discounted {{product_name}}', '<h1>üéâ Great News, {{customer_name}}!</h1><p>The group buy campaign for <strong>{{product_name}}</strong> has reached its goal!</p><p>Your special price: <strong>‚Ç¶{{discount_price}}</strong></p><p><strong>‚è∞ Important:</strong> You have until <strong>{{payment_deadline}}</strong> to complete your payment.</p><p><a href="{{payment_link}}" style="background: #d4af37; color: white; padding: 12px 24px; text-decoration: none; border-radius: 4px; display: inline-block;">Pay Now</a></p>', 'Great News, {{customer_name}}! The group buy for {{product_name}} has reached its goal! Pay ‚Ç¶{{discount_price}} before {{payment_deadline}}. Visit {{payment_link}}'),
('GROUPBUY_SUCCESS_PAID_FINALIZED', 'Group Buy Order Confirmed', 'Congratulations! Your Group Buy Order is Confirmed', '<h1>‚úÖ Order Confirmed, {{customer_name}}!</h1><p>Your group buy order for <strong>{{product_name}}</strong> has been confirmed!</p><p>Order ID: <strong>#{{order_id}}</strong></p><p>We will process your order and send you a shipping notification soon.</p>', 'Order Confirmed! Your group buy order #{{order_id}} for {{product_name}} has been confirmed.'),
('GROUPBUY_FAILED_REFUND', 'Group Buy Campaign Update', 'Notice: Group Buy Campaign Update', '<h1>Campaign Update, {{customer_name}}</h1><p>Unfortunately, the group buy campaign for <strong>{{product_name}}</strong> did not meet its goal.</p><p>Reason: {{reason}}</p><p>If you made a payment, a full refund will be processed within 5-7 business days.</p>', 'The group buy for {{product_name}} did not reach its goal. Reason: {{reason}}. Any payments will be refunded.'),
('GROUPBUY_COMMITMENT_CONFIRMATION', 'Group Buy Commitment Confirmed', 'Your Group Buy Commitment for {{product_name}}', '<h1>Thank you, {{customer_name}}!</h1><p>You have successfully committed to the group buy for <strong>{{product_name}}</strong>.</p><p>Your reserved quantity: <strong>{{quantity}}</strong></p><p>Special price: <strong>‚Ç¶{{discount_price}}</strong></p><p>Current progress: <strong>{{current_quantity}}/{{goal_quantity}}</strong></p>', 'You committed to {{product_name}} group buy. Quantity: {{quantity}} at ‚Ç¶{{discount_price}}.')
