-- Update password_reset template to show 10 minutes expiry
UPDATE email_templates 
SET html_content = '<!DOCTYPE html><html><head><meta charset="utf-8"><meta name="viewport" content="width=device-width,initial-scale=1.0"></head><body style="margin:0;padding:0;background-color:#f8f8f8;font-family:-apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica Neue,Arial,sans-serif;"><table width="100%" cellpadding="0" cellspacing="0" style="background-color:#f8f8f8;padding:40px 20px;"><tr><td align="center"><table width="100%" cellpadding="0" cellspacing="0" style="max-width:600px;background-color:#ffffff;border-radius:8px;overflow:hidden;box-shadow:0 2px 8px rgba(0,0,0,0.05);"><tr><td style="background:linear-gradient(135deg,#1a1a1a 0%,#2d2d2d 100%);padding:32px 40px;text-align:center;"><img src="https://sentra-scent-shop-ai.lovable.app/assets/sentra-logo-BfJ3mVp3.png" alt="Sentra" width="120" style="display:block;margin:0 auto 16px;"/><p style="color:#888;font-size:12px;margin:0;letter-spacing:2px;">PREMIUM FRAGRANCES</p></td></tr><tr><td style="padding:40px;"><h1 style="color:#1a1a1a;font-size:24px;font-weight:600;margin:0 0 24px;text-align:center;">Reset Your Password</h1><p style="color:#333;font-size:16px;line-height:1.6;margin:0 0 16px;">Hi {{name}},</p><p style="color:#555;font-size:15px;line-height:1.6;margin:0 0 32px;">We received a request to reset your password. Click the button below to create a new password:</p><table width="100%" cellpadding="0" cellspacing="0"><tr><td align="center"><a href="{{reset_url}}" style="display:inline-block;background-color:#1a1a1a;color:#ffffff;font-size:16px;font-weight:600;text-decoration:none;padding:16px 48px;border-radius:6px;">Reset Password</a></td></tr></table><p style="color:#888;font-size:14px;line-height:1.6;margin:32px 0 0;"><strong>This link will expire in 10 minutes.</strong> If you didn''t request this, you can safely ignore this email.</p><p style="color:#999;font-size:13px;line-height:1.5;margin:24px 0 0;padding-top:24px;border-top:1px solid #eee;">If the button doesn''t work, copy this link:<br/><a href="{{reset_url}}" style="color:#b8860b;word-break:break-all;">{{reset_url}}</a></p></td></tr><tr><td style="background-color:#fafafa;padding:24px 40px;text-align:center;border-top:1px solid #eee;"><p style="color:#666;font-size:14px;margin:0 0 4px;">To your success,</p><p style="color:#1a1a1a;font-size:14px;font-weight:600;margin:0;">The Sentra Team</p></td></tr></table></td></tr></table></body></html>',
    text_content = 'Hi {{name}}, We received a request to reset your password. Click the link below to create a new password: {{reset_url}} This link will expire in 10 minutes. If you didn''t request this, you can safely ignore this email. To your success, The Sentra Team'
WHERE template_id = 'password_reset';