INSERT INTO public.email_templates (template_id, name, subject, html_content, text_content) VALUES
('INFLUENCER_PROMOTION', 'Influencer Promotion Welcome', 'Welcome to the Sentra Influencer Program, {{name}}!',
  '<!DOCTYPE html><html lang="en"><head><meta charset="UTF-8"><meta name="viewport" content="width=device-width,initial-scale=1"><title>Welcome to the Sentra Influencer Program</title></head><body style="margin:0;padding:0;background-color:#0b0b10;font-family:-apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica,Arial,sans-serif;"><table role="presentation" cellpadding="0" cellspacing="0" width="100%" style="background-color:#0b0b10;padding:24px 0;"><tr><td align="center"><table role="presentation" cellpadding="0" cellspacing="0" width="100%" style="max-width:600px;background:linear-gradient(135deg,#050509,#111827);border-radius:16px;overflow:hidden;border:1px solid rgba(148,163,184,0.25);box-shadow:0 24px 60px rgba(15,23,42,0.75);"><tr><td style="padding:20px 24px 12px 24px;border-bottom:1px solid rgba(148,163,184,0.25);"><table role="presentation" width="100%" cellpadding="0" cellspacing="0"><tr><td align="left" style="color:#e5e7eb;font-size:14px;font-weight:500;letter-spacing:0.08em;text-transform:uppercase;">Sentra</td><td align="right" style="color:#9ca3af;font-size:12px;">Exclusive Influencer Program</td></tr></table></td></tr><tr><td style="padding:24px 24px 8px 24px;color:#f9fafb;"><h1 style="margin:0 0 12px 0;font-size:24px;line-height:1.3;font-weight:600;">Welcome aboard, {{name}} ðŸ‘‹</h1><p style="margin:0 0 12px 0;font-size:14px;line-height:1.6;color:#d1d5db;">You''ve been approved as a Sentra Influencer. You now have access to exclusive perks, rewards and earning opportunities.</p><p style="margin:0 0 16px 0;font-size:14px;line-height:1.6;color:#d1d5db;">Here''s a quick overview of your current status:</p><table role="presentation" cellpadding="0" cellspacing="0" style="width:100%;margin:0 0 16px 0;border-collapse:collapse;"><tr><td style="padding:10px 12px;border-radius:10px;background:rgba(8,47,73,0.85);border:1px solid rgba(56,189,248,0.35);"><p style="margin:0;font-size:13px;color:#e0f2fe;"><strong style="display:block;font-size:13px;margin-bottom:4px;color:#38bdf8;">Your MOQ target</strong>Minimum paid orders required in 30 days: <strong>{{required_moq}}</strong></p></td></tr><tr><td style="height:8px;"></td></tr><tr><td style="padding:10px 12px;border-radius:10px;background:rgba(30,64,175,0.75);border:1px solid rgba(59,130,246,0.5);"><p style="margin:0;font-size:13px;color:#dbeafe;"><strong style="display:block;font-size:13px;margin-bottom:4px;color:#60a5fa;">Your current performance</strong>Paid orders in the last 30 days: <strong>{{paid_orders_30d}}</strong></p></td></tr></table><p style="margin:0 0 18px 0;font-size:14px;line-height:1.6;color:#d1d5db;">Hit or exceed your MOQ to stay active in the program and unlock higher rewards, bonuses and exclusive drops.</p><table role="presentation" cellpadding="0" cellspacing="0" style="margin:0 0 18px 0;"><tr><td align="center" bgcolor="#f97316" style="border-radius:999px;"><a href="{{dashboard_link}}" style="display:inline-block;padding:10px 22px;font-size:14px;font-weight:600;color:#111827;text-decoration:none;border-radius:999px;background:linear-gradient(135deg,#f97316,#facc15);box-shadow:0 10px 25px rgba(248,250,252,0.25);">Open Influencer Dashboard</a></td></tr></table><p style="margin:0 0 12px 0;font-size:13px;line-height:1.6;color:#9ca3af;">From your dashboard you can:</p><ul style="margin:0 0 16px 24px;padding:0;font-size:13px;line-height:1.6;color:#9ca3af;"><li>Track your paid orders and progress toward MOQ</li><li>View your rewards and earning history</li><li>Access assets and content to help you promote Sentra</li></ul><p style="margin:0 0 4px 0;font-size:13px;line-height:1.6;color:#9ca3af;">If you have any questions, reply directly to this email and our team will be happy to help.</p></td></tr><tr><td style="padding:16px 24px 20px 24px;border-top:1px solid rgba(55,65,81,0.9);background:radial-gradient(circle at top,#111827 0,#020617 55%);"><p style="margin:0 0 6px 0;font-size:11px;line-height:1.6;color:#6b7280;">You''re receiving this because your account was upgraded to <strong>Influencer</strong> on Sentra.</p><p style="margin:0 0 4px 0;font-size:11px;line-height:1.6;color:#6b7280;">To stop receiving influencer-related emails, you can <a href="{{unsubscribe_link}}" style="color:#9ca3af;text-decoration:underline;">unsubscribe here</a> or reply with "UNSUBSCRIBE".</p><p style="margin:0;font-size:11px;line-height:1.6;color:#4b5563;">&copy; {{current_year}} Sentra. All rights reserved.</p></td></tr></table></td></tr></table></body></html>',
  'Welcome to the Sentra Influencer Program, {{name}}! You''ve been approved as an Influencer. Your current target is {{required_moq}} paid orders in 30 days. You currently have {{paid_orders_30d}} paid orders. Visit your dashboard: {{dashboard_link}}. To stop receiving influencer emails, click {{unsubscribe_link}} or reply with "UNSUBSCRIBE".'),
('INFLUENCER_MOQ_FAILURE', 'Influencer MOQ Not Met', 'Action needed: MOQ not met for your Sentra Influencer account',
  '<!DOCTYPE html><html lang="en"><head><meta charset="UTF-8"><meta name="viewport" content="width=device-width,initial-scale=1"><title>Influencer MOQ Not Met</title></head><body style="margin:0;padding:0;background-color:#0b0b10;font-family:-apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica,Arial,sans-serif;"><table role="presentation" cellpadding="0" cellspacing="0" width="100%" style="background-color:#0b0b10;padding:24px 0;"><tr><td align="center"><table role="presentation" cellpadding="0" cellspacing="0" width="100%" style="max-width:600px;background:linear-gradient(135deg,#0b1120,#111827);border-radius:16px;overflow:hidden;border:1px solid rgba(248,113,113,0.5);box-shadow:0 24px 60px rgba(15,23,42,0.9);"><tr><td style="padding:20px 24px 12px 24px;border-bottom:1px solid rgba(248,113,113,0.35);"><table role="presentation" width="100%" cellpadding="0" cellspacing="0"><tr><td align="left" style="color:#fecaca;font-size:14px;font-weight:500;letter-spacing:0.08em;text-transform:uppercase;">Sentra</td><td align="right" style="color:#f97316;font-size:12px;">Influencer MOQ Alert</td></tr></table></td></tr><tr><td style="padding:24px 24px 8px 24px;color:#fee2e2;"><h1 style="margin:0 0 12px 0;font-size:22px;line-height:1.3;font-weight:600;">We''ve temporarily disabled your influencer benefits</h1><p style="margin:0 0 12px 0;font-size:14px;line-height:1.6;color:#fecaca;">Hi {{name}}, our latest review shows that the minimum order quantity (MOQ) for your influencer account was not met in the last 30 days.</p><table role="presentation" cellpadding="0" cellspacing="0" style="width:100%;margin:0 0 16px 0;border-collapse:collapse;"><tr><td style="padding:10px 12px;border-radius:10px;background:rgba(153,27,27,0.9);border:1px solid rgba(248,113,113,0.75);"><p style="margin:0;font-size:13px;color:#fee2e2;"><strong style="display:block;font-size:13px;margin-bottom:4px;color:#fed7d7;">Your last 30 days</strong>Paid orders: <strong>{{paid_orders_30d}}</strong> â€¢ Required MOQ: <strong>{{required_moq}}</strong></p></td></tr></table><p style="margin:0 0 12px 0;font-size:14px;line-height:1.6;color:#fecaca;">As a result, your influencer perks and MOQ tracking have been turned off for now. You can always come back and re-activate once you''re ready to ramp up again.</p><p style="margin:0 0 14px 0;font-size:14px;line-height:1.6;color:#fecaca;">Here are a few suggestions to help you bounce back:</p><ul style="margin:0 0 18px 24px;padding:0;font-size:13px;line-height:1.6;color:#fed7d7;"><li>Share your unique links and content more consistently</li><li>Focus on your top performing products and audiences</li><li>Run time-limited offers or bundles to drive urgency</li></ul><table role="presentation" cellpadding="0" cellspacing="0" style="margin:0 0 18px 0;"><tr><td align="center" bgcolor="#f97316" style="border-radius:999px;"><a href="{{dashboard_link}}" style="display:inline-block;padding:10px 22px;font-size:14px;font-weight:600;color:#111827;text-decoration:none;border-radius:999px;background:linear-gradient(135deg,#f97316,#facc15);box-shadow:0 10px 25px rgba(248,250,252,0.3);">Review performance & next steps</a></td></tr></table><p style="margin:0 0 4px 0;font-size:13px;line-height:1.6;color:#9ca3af;">When your activity picks up again, reach out to our team or your account contact and we''ll gladly re-enable your influencer perks.</p></td></tr><tr><td style="padding:16px 24px 20px 24px;border-top:1px solid rgba(75,85,99,0.9);background:radial-gradient(circle at top,#111827 0,#020617 55%);"><p style="margin:0 0 6px 0;font-size:11px;line-height:1.6;color:#9ca3af;">This message relates to performance on your <strong>Sentra Influencer</strong> account and MOQ policy.</p><p style="margin:0 0 4px 0;font-size:11px;line-height:1.6;color:#6b7280;">To stop receiving influencer performance emails, you can <a href="{{unsubscribe_link}}" style="color:#9ca3af;text-decoration:underline;">unsubscribe here</a> or reply with "UNSUBSCRIBE".</p><p style="margin:0;font-size:11px;line-height:1.6;color:#4b5563;">&copy; {{current_year}} Sentra. All rights reserved.</p></td></tr></table></td></tr></table></body></html>',
  'Hi {{name}}, our latest review shows that you did not meet the required MOQ for your Sentra Influencer account. Paid orders in the last 30 days: {{paid_orders_30d}}. Required MOQ: {{required_moq}}. As a result, your influencer perks have been temporarily disabled. You can review your performance and next steps here: {{dashboard_link}}. To stop receiving influencer emails, click {{unsubscribe_link}} or reply with "UNSUBSCRIBE".'),
('INFLUENCER_MOQ_SUCCESS', 'Influencer MOQ Achieved', 'Congrats {{name}}! You met your Sentra Influencer MOQ ðŸŽ‰',
  '<!DOCTYPE html><html lang="en"><head><meta charset="UTF-8"><meta name="viewport" content="width=device-width,initial-scale=1"><title>Influencer MOQ Achieved</title></head><body style="margin:0;padding:0;background-color:#020617;font-family:-apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica,Arial,sans-serif;"><table role="presentation" cellpadding="0" cellspacing="0" width="100%" style="background-color:#020617;padding:24px 0;"><tr><td align="center"><table role="presentation" cellpadding="0" cellspacing="0" width="100%" style="max-width:600px;background:radial-gradient(circle at top,#0f172a 0,#020617 55%);border-radius:16px;overflow:hidden;border:1px solid rgba(34,197,94,0.6);box-shadow:0 24px 60px rgba(6,95,70,0.9);"><tr><td style="padding:20px 24px 12px 24px;border-bottom:1px solid rgba(34,197,94,0.65);"><table role="presentation" width="100%" cellpadding="0" cellspacing="0"><tr><td align="left" style="color:#bbf7d0;font-size:14px;font-weight:500;letter-spacing:0.08em;text-transform:uppercase;">Sentra</td><td align="right" style="color:#4ade80;font-size:12px;">Influencer Milestone</td></tr></table></td></tr><tr><td style="padding:24px 24px 10px 24px;color:#ecfdf5;"><h1 style="margin:0 0 12px 0;font-size:22px;line-height:1.3;font-weight:600;">You did it, {{name}}! ðŸŽ‰</h1><p style="margin:0 0 12px 0;font-size:14px;line-height:1.6;color:#bbf7d0;">You''ve successfully met the minimum order quantity (MOQ) target for your Sentra Influencer account. Amazing work.</p><table role="presentation" cellpadding="0" cellspacing="0" style="width:100%;margin:0 0 16px 0;border-collapse:collapse;"><tr><td style="padding:10px 12px;border-radius:10px;background:rgba(6,78,59,0.95);border:1px solid rgba(34,197,94,0.85);"><p style="margin:0;font-size:13px;color:#dcfce7;"><strong style="display:block;font-size:13px;margin-bottom:4px;color:#bbf7d0;">Milestone summary</strong>Required MOQ: <strong>{{required_moq}}</strong> â€¢ Your paid orders (30d): <strong>{{paid_orders_30d}}</strong></p></td></tr></table><p style="margin:0 0 12px 0;font-size:14px;line-height:1.6;color:#bbf7d0;">Your influencer perks are now active and in good standing. Keep this momentum going to unlock even more rewards and priority access.</p><p style="margin:0 0 14px 0;font-size:14px;line-height:1.6;color:#bbf7d0;">Next steps you can take:</p><ul style="margin:0 0 18px 24px;padding:0;font-size:13px;line-height:1.6;color:#bbf7d0;"><li>Plan your next campaign or content push</li><li>Double down on products your audience loves</li><li>Reach out to us for performance tips and ideas</li></ul><table role="presentation" cellpadding="0" cellspacing="0" style="margin:0 0 18px 0;"><tr><td align="center" bgcolor="#22c55e" style="border-radius:999px;"><a href="{{dashboard_link}}" style="display:inline-block;padding:10px 22px;font-size:14px;font-weight:600;color:#052e16;text-decoration:none;border-radius:999px;background:linear-gradient(135deg,#22c55e,#a3e635);box-shadow:0 10px 25px rgba(22,163,74,0.5);">View your rewards</a></td></tr></table><p style="margin:0 0 4px 0;font-size:13px;line-height:1.6;color:#9ca3af;">If you have ideas, feedback or need strategy support, you can reply directly to this email and our team will assist.</p></td></tr><tr><td style="padding:16px 24px 20px 24px;border-top:1px solid rgba(22,163,74,0.9);background:radial-gradient(circle at top,#022c22 0,#020617 55%);"><p style="margin:0 0 6px 0;font-size:11px;line-height:1.6;color:#6ee7b7;">You''re receiving this because you achieved the required MOQ for your influencer account.</p><p style="margin:0 0 4px 0;font-size:11px;line-height:1.6;color:#6b7280;">To stop receiving influencer performance emails, you can <a href="{{unsubscribe_link}}" style="color:#9ca3af;text-decoration:underline;">unsubscribe here</a> or reply with "UNSUBSCRIBE".</p><p style="margin:0;font-size:11px;line-height:1.6;color:#4b5563;">&copy; {{current_year}} Sentra. All rights reserved.</p></td></tr></table></td></tr></table></body></html>',
  'Congrats {{name}}! You met the required MOQ for your Sentra Influencer account. Required MOQ: {{required_moq}}. Your paid orders in the last 30 days: {{paid_orders_30d}}. Your influencer perks are now active and in good standing. View your rewards and performance here: {{dashboard_link}}. To stop receiving influencer emails, click {{unsubscribe_link}} or reply with "UNSUBSCRIBE".');
